  <tr class=\"row2\">\r\n                                <td>{TXT_TAGS}:</td>\r\n                                <td>\r\n                                    <div>\r\n                                        <!-- BEGIN tags -->\r\n                                        <div>\r\n                                            <ul class=\"tags\">\r\n                                                <!-- BEGIN tag -->\r\n                                                <li><a href=\"index.php?section=knowledge{MODULE_INDEX}&amp;tid={TAGID}\">{TAG}</a>,</li>\r\n                                                <!-- END tag -->\r\n                                                <!-- BEGIN lasttag -->\r\n                                                <li><a href=\"index.php?section=knowledge{MODULE_IDNEX}&amp;tid={TAGID}\">{TAG}</a></li>\r\n                                                <!-- END lasttag -->\r\n                                            </ul>\r\n                                        </div>\r\n                                        <!-- END tags -->\r\n                                    </div>\r\n                                </td>\r\n                            </tr>\r\n                            <tr class=\"row1\">\r\n                                <td>{TXT_CREATED}:</td>\r\n                                <td>{DATE_CREATED}</td>\r\n                            </tr>\r\n                            <tr class=\"row2\">\r\n                                <td>{TXT_LAST_CHANGE}:</td>\r\n                                <td>{DATE_UPDATED}</td>\r\n                            </tr>\r\n                        </tbody>\r\n                    </table>\r\n                </div>\r\n            </div>\r\n            <script type=\"text/javascript\">\r\n                /* <![CDATA[ */\r\n                var sliderOp = baseOptions;\r\n                baseOptions.counter = \'{COUNTER}\';\r\n                slider_{ARTICLE_ID}{COUNTER} = new Slider(\"{ARTICLE_ID}\", sliderO