 						<li><a href=\"index.php?section=knowledge{MODULE_IDNEX}&amp;tid={TAGID}\">{TAG}</a></li>\r\n    					<!-- END lasttag -->\r\n    					</ul>\r\n    				</div>\r\n    				<!-- END tags -->\r\n    			</div>\r\n    			</td>\r\n    		</tr>\r\n    		<tr class=\"row1\">\r\n    			<td>{TXT_CREATED}:</td>\r\n    			<td>{DATE_CREATED}</td>\r\n    		</tr>\r\n    		<tr class=\"row2\">\r\n    			<td>{TXT_LAST_CHANGE}:</td>\r\n    			<td>{DATE_UPDATED}</td>\r\n    		</tr>\r\n    		<tr class=\"row1\">\r\n    			<td>Article ID:</td>\r\n    			<td>{ARTICLEID}</td>\r\n    		</tr>\r\n    	</table>\r\n    </div>\r\n</div>\r\n	\r\n\r\n<script type=\"text/javascript\">\r\n/* <![CDATA[ */\r\n\r\nrating = new Rating({ARTICLEID}, {AVERAGE}, { \r\n	stars : {MAX_RATING}, \r\n	starPath : \'images/modules/knowledge/\',\r\n	elemPrefix : \'rating_elem_\',\r\n	onRate : function(data, rating) {\r\n		var foo = new Ajax.Request(\'index.php?section=knowledge&act=rate\', {\r\n			method: \"post\",\r\n			parameters : {\r\n				id : data.nr,\r\n				rated : rating\r\n			}\r\n		});\r\n	},\r\n	callbackData : {nr : {ARTICLEID}}\r\n	});\r\n/*\r\nnew Starbox(\'rating\', {AVERAGE}, { \r\n	overlay: \'pointy.png\', \r\n	className: \'pointy\', \r\n	total: 1,\r\n	stars: {MAX_RATING},\r\n	max: {MAX_RATING},\r\n	buttons: {MAX_RATING},\r\n	onRate : function(element, info) {\r\n		info.id = {ARTICLEID};\r\n		new Ajax.Request(\'index.php?section=knowledge{MODULE_INDEX}&act=rate\', {\r\n			method: \"post\",\r\n			parameters : info\r\n		});\r\n	}\r\n});\r\n*/\r\n/* ]]> */\r\n</script>\r\n\r\n\r\n',1,'',NULL,'',NULL,0,NULL,'Artikel','Artikel','Artikel','index',NULL,NULL,'',0,0,0,0,1,'','knowledge','article');
